import React, { Component } from 'react';
import ReactDOM from "react-dom";
import "./assets/style.css";
import quizService from "./quizService";
import QuestionBox from "./components/QuestionBox";

class QuizBee extends Component {
	state = {
		questionBank: [],
		score: 0,
		responses: 0
	};

	getQuestions = () => {
		quizService().then(question => {
			this.setState({
				questionBank: question
			});
		});
	};

	computedAnswer = (answer, correctAnswer) => {
		if (answer === correctAnswer) {
			// add +1 to current score using setState
			this.setState({
				score: this.state.score + 1
			});
		}
		// increment responses by 1
		this.setState({
			responses: this.state.responses < 5 ? this.state.responses + 1 : 5
		})
	}
	componentDidMount() {
		this.getQuestions();
	}

	render() {
		return (
			<div className="container">
				<div className="title">QuizBee</div>
				{this.state.questionBank.length > 0 && this.state.questionBank.map(
					({question, answers, correct, questionId}) => 
						<QuestionBox 
							question={question} 
							options={answers} 
							key={questionId} 
							selected={answer => this.computedAnswer(answer, correct)}
						/>
				)}
			</div>
		);
	}
}

ReactDOM.render( <QuizBee />, document.getElementById("root") );
